const showMenu=(toggleId,navId)=>{const toggle=document.getElementById(toggleId),nav=document.getElementById(navId);toggle&&nav&&toggle.addEventListener("click",()=>{nav.classList.toggle("show-menu")})};showMenu("nav-toggle","nav-menu");const navLink=document.querySelectorAll(".nav__link");function LinkAction(){const navMenu=document.getElementById("nav-menu");navMenu.classList.remove("show-menu")}navLink.forEach(e=>e.addEventListener("click",LinkAction));const sections=document.querySelectorAll("section[id]");function scrollActive(){const scrollY=window.pageYOffset;try{sections.forEach(current=>{const sectionHeight=current.offsetHeight,sectionTop=current.offsetTop-50;sectionId=current.getAttribute("id"),scrollY>sectionTop&&scrollY<=sectionTop+sectionHeight?document.querySelector(".nav__menu a[href*="+sectionId+"]").classList.add("active-link"):document.querySelector(".nav__menu a[href*="+sectionId+"]").classList.remove("active-link")})}catch(e){}}function scrollHeader(){const nav=document.getElementById("header");this.scrollY>=200?nav.classList.add("scroll-header"):nav.classList.remove("scroll-header")}function scrollTop(){const nav=document.getElementById("scroll-top");this.scrollY>=560?nav.classList.add("show-scroll"):nav.classList.remove("show-scroll")}function initMap(){const uluru={lat:-25.344,lng:131.036},map=new google.maps.Map(document.getElementById("map"),{zoom:13,center:uluru}),marker=new google.maps.Marker({position:uluru,map:map})}window.addEventListener("scroll",scrollActive),window.addEventListener("scroll",scrollHeader),window.addEventListener("scroll",scrollTop);